import React, { useEffect, useState } from "react";
import "./ContactPage.css";
import Header from "../Header/Header";
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBModal, MDBModalBody } from "mdbreact";
import axios from "axios";
import Constants from "../../configurations/Constants";
import Button from '@material-ui/core/Button';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import { makeStyles } from '@material-ui/core/styles';

function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    '& > * + *': {
      marginTop: theme.spacing(2),
    },
  },
}));

const ContactPage = () => {
  let URL= Constants.BASE_URL_1;
  const [nameInput, setName] = useState("");
  const [emailInput, setEmail] = useState("");
  const [messageInput, setMessage] = useState("");
  const [modal10, setModal10] = useState(false);
  const [modal11, setModal11] = useState(false);
  const [success, setSuccess] = useState("");

  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  const handleClick = () => {
    setOpen(true);
  };

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }

    setOpen(false);
  };

  const toggle = nr => () => {
    let modalNumber = 'modal' + nr
    if(modalNumber === 'modal10') {
      setModal10(!modal10)
    }
    if(modalNumber === 'modal11') {
      setModal11(!modal11)
    }
  }

  const handleNameInput = (event) => {
    setName(event.target.value);
  };

  const handleEmailInput = (event) => {
    setEmail(event.target.value);
  };

  const handleMessageInput = (event) => {
    setMessage(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (
      nameInput !== "" &&
      emailInput !== "" &&
      messageInput !== "" &&
      messageInput.length >= 10
    ) {
      const data = {
        name: nameInput,
        email: emailInput,
        message: messageInput,
      };

      axios
        .post(URL + "more/enquiry", data)
        .then((res) => {
          setSuccess(res.data.body);
          setName("");
          setEmail("")
          setMessage("");
        });
      
    }
  };

  return (
    <div>
      <Header />
      <MDBContainer>
        <MDBRow>
          <MDBCol className="form-body" sm="8" md="6" lg="5" xl="5">
            <form onSubmit={handleSubmit}>
              <p className="h3 text-center mb-4">Get In Touch</p>
              <p className="h6 text-center mb-4 form-text">
                We're here for you, and we're wearing our thinking caps. Feel
                free to contact us regarding your queries.
              </p>
              <label htmlFor="defaultFormContactNameEx" className="grey-text">
                Name...
              </label>
              <input
                type="text"
                id="defaultFormContactNameEx"
                className="form-control"
                onChange={handleNameInput}
              />
              <br />
              <label htmlFor="defaultFormContactEmailEx" className="grey-text">
                Email...
              </label>
              <input
                type="email"
                id="defaultFormContactEmailEx"
                className="form-control"
                onChange={handleEmailInput}
              />
              <br />
              <label
                htmlFor="defaultFormContactMessageEx"
                className="grey-text"
              >
                Message... (10 character minimum)
              </label>
              <textarea
                type="text"
                id="defaultFormContactMessageEx"
                className="form-control"
                rows="5"
                onChange={handleMessageInput}
              />
              <div className="text-center mt-4">
                <MDBBtn className="form-btn btn1" onClick={toggle(11)} type="submit">
                  Send
                </MDBBtn>
              </div>
            </form>
          </MDBCol>
        </MDBRow>

        <div className={classes.root}>
      <Button variant="outlined" onClick={handleClick}>
        Open success snackbar
      </Button>
      <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
        <Alert onClose={handleClose} severity="success">
          This is a success message!
        </Alert>
      </Snackbar>
      <Alert severity="success">This is a success message!</Alert>
    </div>

        <MDBModal isOpen={modal11} toggle={toggle(11)} frame position="top">
          <MDBModalBody className="text-center">
            {success}
            <MDBBtn className="btn2" onClick={toggle(11)}>Got it!</MDBBtn>
          </MDBModalBody>
        </MDBModal>
      </MDBContainer>
    </div>
  );
};

export default ContactPage;
