import React from "react";
import CourseChapters from "./CourseChapters/CourseChapters";
import "./CourseChapters/CourseChapters.css";
import "./CourseDetails.css"
import { Container } from "react-bootstrap";

const CourseDetail = () => {
  const ab = [
    {
      lecture: "LECTURE 1.1",
      title: "Introduction to this Course",
      data: ["A Note On Asking For Help - 1", "A Note On Asking For Help - 2", "A Note On Asking For Help - 3"]
    },
    {
      lecture: "LECTURE 1.2",
      title: "Introduction to this Course - 1",
      data: ["A Note On Asking For Help - 4", "A Note On Asking For Help - 5", "A Note On Asking For Help - 6"]
    },
    {
      lecture: "LECTURE 1.3",
      title: "Introduction to Web Development",
      data: ["A Note On Asking For Help - 7", "A Note On Asking For Help - 8", "A Note On Asking For Help - 9"]
    }
  ]

  return (
    <React.Fragment>
      <Container className = "details-container">
        <h1 className="heading">Curriculum</h1>
        
        <div className="course-chapters">
          <hr style={{width: "100%"}} />
          {ab.map((a) => {
            return (
              <div>
                <CourseChapters title={a.title} lecture={a.lecture}>
                  {a.data.map((data) => {
                    return (
                    <div>
                      <p style={{paddingLeft: 20}}>{data}</p>
                      <hr />
                    </div>
                    )
                  })}
                  {/* <span className="accordion-text">A Note On Asking For Help</span> */}
                </CourseChapters>
                <hr style={{width: "100%"}} />
              </div>
            )
          })}
          
        </div>
      </Container>
    </React.Fragment>
  );
};

export default CourseDetail;
