import React, {useEffect, useState} from "react";
import "./HomePageCoursesList.css";
import {Col, Container, Row} from "react-bootstrap";
import {Card, Icon, Image} from "semantic-ui-react";
import "semantic-ui-css/semantic.min.css";
import StarRatings from "react-star-ratings";
import axios from 'axios'
import {CoursesSlider as Courses} from "../../styles/CoursesSliderStyle";

const HomePageCoursesList = (props) => {
    const dataSource = props.Data;
    const Title = props.Title
    const [courses, setCourses] = useState([]);
    var index = 0;

    useEffect(() => {
        axios.get("http://dev-api.courseali.tech" + dataSource)
            .then((results) => {
                setCourses(results.data.body);
            });
    }, []);

    return (
        <div>
            <Container id="course-list-container" className="abc">
                <Courses>
                    <Row className="courses-heading">
                        <Col>{Title}</Col>
                    </Row>
                    <Row xs={1} sm={2} md={3} lg={4}>
                        {courses.map((course) => {
                            let image;
                            let author_image;
                            if (course.courseMedia[0].course_media === "") {
                                image = (
                                    <Image
                                        className="course-card-image"
                                        src={
                                            "https://course-ali.s3.amazonaws.com/media/course_4.1.jpg"
                                        }
                                        wrapped
                                        ui={false}
                                    />
                                );
                            } else {
                                image = (
                                    <Image
                                        className="course-card-image"
                                        src={
                                            "https://course-ali.s3.amazonaws.com/media/" +
                                            course.courseMedia[0].course_media
                                        }
                                        wrapped
                                        ui={false}
                                    />
                                );
                            }
                            if (course.ab_authors.profile_url === "") {
                                author_image = (<img alt=""
                                                     className="course-author-pic"
                                                     src="../assets/author_placeholder.png"/>)
                            } else if (course.ab_authors.profile_url !== "") {
                                author_image = (<img alt=""
                                                     className="course-author-pic"
                                                     src={"https://course-ali.s3.amazonaws.com/media/" + course.ab_authors.profile_url}
                                />)
                            }

                            return (
                                <Col key={index++} className="course-card-grid">
                                    <Card className="course-card">
                                        {image}
                                        <Card.Content>
                                            <Card.Meta>
                                                {author_image}
                                            </Card.Meta>
                                            <Card.Meta
                                                style={{
                                                    paddingTop: 12,
                                                    paddingBottom: 10,
                                                    fontSize: 10,
                                                }}
                                            >
                                                <div style={{height: 20}}>
                        <span id="course-card-author">
                          {course.ab_authors.name}
                        </span>
                                                    <span id="course-card-amount">
                          {"$"}{course.course_price_data.amount}
                        </span>
                                                </div>
                                            </Card.Meta>

                                            <p className="course-card-title">{course.course_title}</p>
                                            <Card.Description>
                                                <StarRatings
                                                    rating={parseFloat(
                                                        course.course_statistics.ratings.$numberDecimal
                                                    )}
                                                    starRatedColor="#48c52b"
                                                    numberOfStars={5}
                                                    starDimension="20px"
                                                    starSpacing="2px"
                                                />
                                            </Card.Description>
                                        </Card.Content>
                                        <Card.Content extra>
                    <span>
                    <span id="course-card-extra-stats">
                      <Icon name="users"/>
                        {course.course_statistics.students_enrolled} {" "}
                    </span>
                        {/* <span id="course-card-amount"> */}
                        {/* <strong>{course.source.name}</strong> */}
                        <Image className="course-card-source-logo"
                               src={"https://course-ali.s3.amazonaws.com/media/" + course.source.logo}></Image>
                        {/* </span> */}
                    </span>
                                        </Card.Content>
                                    </Card>
                                </Col>
                            );
                        })}
                    </Row>
                </Courses>
            </Container>
        </div>
    );
};

export default HomePageCoursesList;
