import React, {useEffect, useState} from "react";
import CourseChapters from "./CourseChapters";
import "./CourseChapters.css";
import "./CourseChapterDetails.css"
import { Container } from "react-bootstrap";

const CourseChapterDetails = (props) => {
  const ab = [
    {
      lecture: "LECTURE 1.1",
      title: "Introduction to this Course",
      data: ["A Note On Asking For Help - 1", "A Note On Asking For Help - 2", "A Note On Asking For Help - 3"]
    },
    {
      lecture: "LECTURE 1.2",
      title: "Introduction to this Course - 1",
      data: ["A Note On Asking For Help - 4", "A Note On Asking For Help - 5", "A Note On Asking For Help - 6"]
    },
    {
      lecture: "LECTURE 1.3",
      title: "Introduction to Web Development",
      data: ["A Note On Asking For Help - 7", "A Note On Asking For Help - 8", "A Note On Asking For Help - 9"]
    }
  ]
  let line;
  const [courseContent, setContent] = useState([])
  const [duration, setDuration] = useState("")

  const convert_time_to_string = (days, hours, minutes) => {
    return String((Number(days) * 24) * 60 + Number(hours) + ":" + Number(minutes))
  }

  useEffect(() => {
    setContent(props.courseContent)
  }, []);

  return (
    <React.Fragment>
      <Container className = "details-container">
        <h1 className="heading">Curriculum
          <span id="heading-total-time">40: 32</span>
          <span id="heading-no-of-lecture">Lectures</span>
          <span id="no-of-lectures">3 &nbsp;</span>
        </h1>
        
        <div className="course-chapters">
          <hr style={{width: "100%"}} />
          {ab.map((a) => {
            return (
              <div>
                {courseContent.reverse().map((content) => {
                  
                  return (
                    <CourseChapters title={content.name} lecture={"1." + content.id}
                      duration={convert_time_to_string(content.duration.days, content.duration.hours, content.duration.minutes)}
                    >
                      {/* {content.chapters} */}
                    </CourseChapters>
                  )
                })}
                {/* <CourseChapters title={a.title} lecture={a.lecture} duration={"5m"}>
                  {a.data.map((data, index) => {
                    if(index === a.data.length -1) {
                      line = <div></div>
                    }
                    else {
                      line = <hr />
                    }
                    return (
                      <div>
                        <span style={{paddingLeft: 20}}>{data}</span>
                        <span style={{float: "right"}}>5:28m</span>
                        {line}
                      </div>
                    )
                  })}
                </CourseChapters> */}
                <hr style={{width: "100%"}} />
              </div>
            )
          })}
          
        </div>
      </Container>
    </React.Fragment>
  );
};

export default CourseChapterDetails;
