import React, {useEffect, useState} from "react";
import "./HomePageCoursesSlider.css";
import axios from "axios"
import {Col, Container, Row} from "react-bootstrap";
import {Card, Image} from "semantic-ui-react";
import "semantic-ui-css/semantic.min.css";
import {CoursesSlider} from "../../styles/CoursesSliderStyle";
import Slider from "react-slick";

const HomePageCoursesSlider = (props) => {
    const dataSource = props.Data;
    const Title = props.Title;
    const [newCourses, setNewCourses] = useState([]);

    useEffect(() => {
        axios.get("http://dev-api.courseali.tech" + dataSource)
            .then((results) => {
                setNewCourses(results.data.body);
                console.log(results.data.body)
            });
    }, []);

    const newCoursesSliderSettings = {
        infinite: false,
        speed: 500,
        nextArrow: <div><img alt="" height={30} src="../assets/next.svg"/></div>,
        prevArrow: <div><img alt="" style={{transform: "rotate(180deg)"}} height={30} src="../assets/next.svg"/></div>,
        slidesToShow: 4,
        slidesToScroll: 1,
        responsive: [
            {
                breakpoint: 980, // tablet breakpoint
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 1,
                },
            },
            {
                breakpoint: 777, // tablet breakpoint
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                },
            },
            {
                breakpoint: 550, // mobile breakpoint
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                },
            },
        ],
    };
    return (
        <div>
            <Container fluid className="abc new-courses-container">

                <CoursesSlider>
                    <Row className="new-courses-heading">
                        <Col>{Title}</Col>
                    </Row>

                    <Row className="new-courses-row">
                        <Col className="new-courses-slider">

                            <Slider {...newCoursesSliderSettings}>
                                {newCourses.map((course) => {
                                    let image;
                                    let author_image;
                                    if (course.courseMedia[0].course_media === "") {
                                        image = (
                                            <Image
                                                className="course-card-image"
                                                src={
                                                    "https://course-ali.s3.amazonaws.com/media/course_4.1.jpg"
                                                }
                                                wrapped
                                                ui={false}
                                            />
                                        );
                                    } else {
                                        image = (
                                            <Image
                                                className="course-card-image"
                                                src={
                                                    "https://course-ali.s3.amazonaws.com/media/" +
                                                    course.courseMedia[0].course_media
                                                }
                                                wrapped
                                                ui={false}
                                            />
                                        );
                                    }
                                    if (course.ab_authors.profile_url === "") {
                                        author_image = (<img alt=""
                                                             className="course-author-pic"
                                                             src="../assets/author_placeholder.png"/>)
                                    } else if (course.ab_authors.profile_url !== "") {
                                        author_image = (<img alt=""
                                                             className="course-author-pic"
                                                             src={"https://course-ali.s3.amazonaws.com/media/" + course.ab_authors.profile_url}
                                        />)
                                    }
                                    return (
                                        <div key={course._id}>
                                            <Card className="new-course-card">
                                                {image}
                                                <Card.Content>
                                                    <Card.Meta>
                                                        {author_image}
                                                    </Card.Meta>
                                                    <Card.Meta
                                                        style={{
                                                            paddingTop: 12,
                                                            paddingBottom: 10,
                                                            fontSize: 10,
                                                        }}
                                                    >
                                                        <div style={{height: 20}}>
                              <span id="new-course-card-author">
                                {course.ab_authors.name}
                              </span>
                                                            <span id="new-course-card-amount">
                                {"$"} {course.course_price_data.amount}
                              </span>
                                                        </div>
                                                    </Card.Meta>

                                                    <p className="new-course-card-title">
                                                        {course.course_title}
                                                    </p>
                                                    <Card.Description className="new-course-card-source">
                                                        <Image
                                                            className="new-course-card-source-logo"
                                                            src={"https://course-ali.s3.amazonaws.com/media/" + course.source.logo}
                                                        ></Image>
                                                    </Card.Description>
                                                </Card.Content>

                                            </Card>
                                        </div>
                                    );
                                })}
                            </Slider>

                        </Col>
                    </Row>
                </CoursesSlider>
            </Container>
        </div>
    );
};

export default HomePageCoursesSlider;
