import styled from "styled-components";

const CourseCard = styled.div`
  .card {
      border-radius: 0px;
  }

  .ui.card {
    border-radius: 0px;
    width: auto;
    max-width: 350px;
  }

  .ui.card > :first-child {
      border-radius: 0px;
  }
`;

export default CourseCard
