import React, { useEffect, useState } from "react";
import "./HomePage.css";
import { MarginRemover } from "../../styles/MarginRemover";
import axios from "axios";

import "semantic-ui-css/semantic.min.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "font-awesome/css/font-awesome.min.css";

import Ribbon from "../Ribbon/Ribbon";
import Header from "../Header/Header";
import FullWidthSlider from "../MainSlider/FullWidthSlider";
import HomePageCoursesList from "../CoursesList/HomePageCoursesList";
import HomePageDeals from "../DealsSlider/HomePageDeals";
import HomePageCoursesSlider from "../CoursesSlider/HomePageCoursesSlider";
import Constants from "../../configurations/Constants";
import { useParams, useHistory } from "react-router-dom";

const HomePage = () => {
  const [Responses, setResponses] = useState([]);
  const [PROMO_RIBBON, setA] = useState([]);
  let len = 0;
  let len1 = 0;

  let { pageId } = useParams();
  let history = useHistory();

  useEffect(() => {
    let url = Constants.COMPONENT_LIST_BASE_URL;
    if (pageId) {
      url = url + pageId;
    } else {
      url = url + "homepage";
    }
    axios.get(url).then(
      (results) => {
        setResponses(results.data.body.result);
        setA(results.data.body.result[0].data);

        console.log(results.data.body.result);

        {
          results.data.body.result.map((slider) => {
            if (slider.componentType === "FULL_WIDTH_SLIDER") {
              len = slider.data.sliderData.length;
              len1 = len * 2;
              
              
              // slider.data.sliderData.map((sliderData) => {
              //   if (
              //     sliderData.subHeaderHtml !== undefined &&
              //     sliderData.titleTextHtml !== undefined
              //   ) {
              //     document.getElementById(++len).dangerouslySetInnerHTML={__html: sliderData.subHeaderHtml};
              //     document.getElementById(++len1).dangerouslySetInnerHTML={__html: sliderData.titleTextHtml};
              //   } else if (
              //     sliderData.subHeaderHtml === undefined &&
              //     sliderData.titleTextHtml !== undefined
              //   ) {
              //     document.getElementById(++len).dangerouslySetInnerHTML={__html: <p></p>};
              //     document.getElementById(++len1).dangerouslySetInnerHTML={__html: sliderData.titleTextHtml};
              //   } else if (
              //     sliderData.subHeaderHtml !== undefined &&
              //     sliderData.titleTextHtml === undefined
              //   ) {
              //     document.getElementById(++len).dangerouslySetInnerHTML={__html: sliderData.subHeaderHtml};
              //     document.getElementById(++len1).dangerouslySetInnerHTML={__html: <p></p>};
              //   }
              // });
            }
          });
        }
      },
      (error) => {
        console.log(error);
        history.push("/error");
      }
    );
  }, []);

  return (
    <div className="HomePage">
      <MarginRemover>
        <Ribbon PROMO_RIBBON={PROMO_RIBBON} />
        <Header />
        {Responses.map((response) => {
          if (response.componentType === "FULL_WIDTH_SLIDER") {
            return <FullWidthSlider slider={response.data.sliderData} />;
          } else if (
            response.componentType === "COURSE_GRID_FIT_WIDTH_MULTI_ROW"
          ) {
            return (
              <HomePageCoursesList
                Title={response.data.sectionTitle}
                Data={response.data.dataSourceEndPoint}
              />
            );
          } else if (response.componentType === "DEAL_IMAGE_DOUBLE_SCROLL") {
            return (
              <HomePageDeals
                Title={response.data.sectionTitle}
                Deals={response.data.offers}
              />
            );
          } else if (
            response.componentType === "COURSE_GRID_FIT_WIDTH_SCROLL"
          ) {
            return (
              <HomePageCoursesSlider
                Title={response.data.sectionTitle}
                Data={response.data.dataSourceEndPoint}
              />
            );
          }
        })}
      </MarginRemover>
    </div>
  );
};

export default HomePage;
