import React, { useEffect, useState } from "react";
import axios from "axios";
import CourseChapterDetails from "./CourseChapters/CourseChapterDetails";
import "./CourseDetails.css";
import { Container, Row, Col } from "react-bootstrap";
import Constants from "../../configurations/Constants";
import { Card, Button } from "semantic-ui-react";
import { CourseDetailsStyle, CourseDetailsPromoStyle, CourseInstructorStyle } from "../../styles/CourseDetailsStyle"
import StarRatings from "react-star-ratings";

const CourseDetail = () => {
  const [courseDetails, setCourseDetails] = useState([]);
  const [M, setM] = useState("");
  const [duration, setDuration] = useState(0);
  const [classCount, setClassCount] = useState(0);
  const [category, setCategory] = useState("")
  const [rating, setRating] = useState(0)
  const [ratingCount, setRatingCount] = useState(0)
  const [courseAmount, setCourseAmount] = useState("")
  const [courseDiscountedAmount, setCourseDiscountedAmount] = useState("")
  const [promotionText, setPromotionText] = useState("")
  const [promotionLink, setPromotionLink] = useState("")
  const [authorData, setAuthorData] = useState({})

  useEffect(() => {
    let url = Constants.COURSE_DETAILS_BASE_URL;
    let class_count = 0;
    axios.get(url + "1").then((results) => {
      setCourseDetails(results.data.body[0]);
      setM(results.data.body[0].courseMedia[0].course_media);
      console.log(results.data.body[0]);
      setDuration(Number(results.data.body[0].duration.days) * 24 + Number(results.data.body[0].duration.hours));
      {results.data.body[0].course_content.map((m) => {
        class_count = class_count + m.chapters.length
        setClassCount(class_count)
      })}
      setCategory(results.data.body[0].ab_categories[0].name)
      setRating(results.data.body[0].course_statistics.ratings.$numberDecimal)
      setRatingCount(results.data.body[0].course_statistics.external_review_counts.$numberDecimal)
      setCourseAmount(results.data.body[0].course_price_data.amount)
      setCourseDiscountedAmount(results.data.body[0].course_promotions[0].discount_amount.$numberDecimal)
      setPromotionText(results.data.body[0].course_promotions[0].promotion_text)

      if (results.data.body[0].course_promotions[0].click_link !== null) {
        setPromotionLink(results.data.body[0].course_promotions[0].click_link)
      } else {
        setPromotionLink("#")
      }

      setAuthorData(results.data.body[0].ab_authors)
    });
  }, []);

  return (
    <React.Fragment>
      <img
        className="course-details-top"
        src="./assets/course-details-top.png"
      />

      {courseDetails && (
        <Container>
          <Row className="course-top-row">
            <Col className="course-title" lg={7}>
              <p>{courseDetails.course_title}</p>
            </Col>
            <Col className="intsructor" lg={{span: 4, offset: 1}}>
              <p id="intsructor-heading">Instructor Info</p>
              <CourseInstructorStyle>
                <Card>
                  <Card.Content style={{marginTop: 10}}>
                      <span style={{marginRight: "30px", marginLeft: "20px"}}>
                        <img id="instructor-image" src={Constants.MEDIA_BASE_URL + authorData.profile_url} />
                      </span>
                      <span style={{marginRight: "auto"}}>
                        <p id="instructor-name">{authorData.name}</p>
                        <p id="instructor-intro">{authorData.intro_line}</p>
                      </span>
                  </Card.Content>
                </Card>
              </CourseInstructorStyle>
            </Col>
          </Row>
          <CourseDetailsStyle>
            <Row className="course-info-row">
            
            <Card className="my-auto course-info-row">
              <Card.Content className="course-info-row-content">
                <span className="course-info-row-content-1">
                  <img src="./assets/duration.svg" />
                </span>
                <span className="course-info-row-content-2">
                  <p id="course-info-row-content-2-1">Duration</p>
                  <span>{classCount} Classes - {duration} Hours</span>
                </span>
              </Card.Content>
            </Card>

            <Card className="my-auto course-info-row">
              <Card.Content className="course-info-row-content">
                <span className="course-info-row-content-1">
                  <img src="./assets/category.svg" />
                </span>
                <span className="course-info-row-content-2">
                  <p id="course-info-row-content-2-1">Categories</p>
                  <span>{category}</span>
                </span>
              </Card.Content>
            </Card>

            <Card className="my-auto course-info-row">
              <Card.Content className="course-info-row-content">
                <span className="course-info-row-content-1">
                  <img src="./assets/rating.svg" />
                </span>
                <span className="course-info-row-content-2">
                  <span>{rating}</span><span id="course-info-row-content-2-1">({parseFloat(ratingCount)})</span>
                  <span>
                    <StarRatings
                        rating={parseFloat(
                          rating
                        )}
                        starRatedColor="#FFC208"
                        numberOfStars={5}
                        starDimension="20px"
                        starSpacing="2px"
                      />
                      </span>
                </span>
              </Card.Content>
            </Card>
            
          </Row>
          </CourseDetailsStyle>

          <CourseDetailsPromoStyle>
            <Row className="course-promotion-row">
              <Card>
                <Card.Content>
                <span className="course-promotion-content-1">
                  <span id="course-promotion-content-1-1">{courseAmount}</span>
                  <span id="course-promotion-content-1-2">&nbsp; ${courseDiscountedAmount}</span>
                </span>
                <span className="course-promotion-content-2">
                  <span>
                    <Button onClick={() => window.open(promotionLink)} id="course-promotion-content-2-1">
                      {promotionText}
                    </Button>
                  </span>
                  {/* <span>
                    <Button id="course-promotion-content-2-2">
                      
                    </Button>
                  </span> */}
                </span>
                </Card.Content>
              </Card>
            </Row>
          </CourseDetailsPromoStyle>
          <Row className="course-image-row">
            <Col>
              <img height="400px" src={Constants.MEDIA_BASE_URL + M} />
            </Col>
          </Row>
        </Container>
      )}
      {/* <CourseChapterDetails /> */}
    </React.Fragment>
  );
};

export default CourseDetail;
