import React, { useEffect, useState } from "react";
import "./ContactPage.css";
import Header from "../Header/Header";
import { MDBContainer, MDBRow, MDBCol, MDBBtn} from 'mdbreact';
import axios from "axios";
import Constants from '../../configurations/Constants'

const ContactPage = () => {

  let URL = Constants.BASE_URL;
  const [nameInput, setName] = useState('')
  const [emailInput, setEmail] = useState('')
  const [messageInput, setMessage] = useState('')

  const handleNameInput = event => {
    setName(event.target.value)
    console.log(event.target.value)
  }

  const handleEmailInput = event => {
    setEmail(event.target.value)
  }

  const handleMessageInput = event => {
    setMessage(event.target.value)
  }

  const handleSubmit = (event) => {
    event.preventDefault();

    const data = {
      name: nameInput,
      email: emailInput,
      message: messageInput
    }

    axios.post("https://dev-api.courseali.tech/more/enquiry", data)
      .then(res => {
        console.log(res)
        console.log(res.data)
      })
  }

  return (
    <div>
      <Header />
      <MDBContainer>
        <MDBRow>
          <MDBCol className="form-body" sm="8" md="6" lg="5" xl="5">
            <form onSubmit={handleSubmit}>
              <p className="h3 text-center mb-4">Get In Touch</p>
              <p className="h6 text-center mb-4 form-text">
                We're here for you, and we're wearing our thinking caps. Feel free to contact us regarding your queries.
              </p>
              <label htmlFor="defaultFormContactNameEx" className="grey-text">
                Name...
              </label>
              <input
                type="text"
                id="defaultFormContactNameEx"
                className="form-control"
                onChange={handleNameInput}
              />
              <br />
              <label htmlFor="defaultFormContactEmailEx" className="grey-text">
                Email...
              </label>
              <input
                type="email"
                id="defaultFormContactEmailEx"
                className="form-control"
                onChange={handleEmailInput}
              />
              <br />
              <label
                htmlFor="defaultFormContactMessageEx"
                className="grey-text"
              >
                Message... (10 character minimum)
              </label>
              <textarea
                type="text"
                id="defaultFormContactMessageEx"
                className="form-control"
                rows="5"
                onChange={handleMessageInput}
              />
              <div className="text-center mt-4">
                <MDBBtn className="form-btn" type="submit">
                  Send
                </MDBBtn>
              </div>
            </form>
          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </div>
  );
};

export default ContactPage;
